import React from 'react';
import { 
  Box, 
  Container, 
  Typography, 
  Grid, 
  Card, 
  CardContent,
  Button,
  useTheme,
  IconButton,
  Paper,
  List,
  ListItem,
  ListItemText,
  ListItemIcon,
  Chip,
  Link,
} from '@mui/material';
import { motion } from 'framer-motion';
import SchoolIcon from '@mui/icons-material/School';
import EventIcon from '@mui/icons-material/Event';
import LibraryBooksIcon from '@mui/icons-material/LibraryBooks';
import ArrowForwardIcon from '@mui/icons-material/ArrowForward';
import { Swiper, SwiperSlide } from 'swiper/react';
import { Navigation, Pagination, Autoplay } from 'swiper/modules';
import 'swiper/css';
import 'swiper/css/navigation';
import 'swiper/css/pagination';
import { styled } from '@mui/material/styles';
import GroupIcon from '@mui/icons-material/Group';
import WorkIcon from '@mui/icons-material/Work';
import ScienceIcon from '@mui/icons-material/Science';
import NotificationsIcon from '@mui/icons-material/Notifications';
import CalendarTodayIcon from '@mui/icons-material/CalendarToday';
import NewspaperIcon from '@mui/icons-material/Newspaper';
import EmojiEventsIcon from '@mui/icons-material/EmojiEvents';
import { Link as RouterLink } from 'react-router-dom';

const HeroSection = styled(Box)(({ theme }) => ({
  position: 'relative',
  overflow: 'hidden',
  '& .swiper': {
    width: '100%',
    height: '600px',
    [theme.breakpoints.down('md')]: {
      height: '400px',
    },
  },
  '& .swiper-slide img': {
    width: '100%',
    height: '100%',
    objectFit: 'cover',
  },
  '& .swiper-pagination-bullet': {
    width: '12px',
    height: '12px',
    background: '#fff',
    opacity: 0.5,
    '&-active': {
      opacity: 1,
      background: theme.palette.primary.main,
    },
  },
  '& .swiper-button-prev, & .swiper-button-next': {
    color: '#fff',
    background: 'rgba(0,0,0,0.3)',
    width: '40px',
    height: '40px',
    borderRadius: '50%',
    '&::after': {
      fontSize: '20px',
    },
    '&:hover': {
      background: theme.palette.primary.main,
    },
  },
}));

const AnnouncementCard = styled(Paper)(({ theme }) => ({
  padding: theme.spacing(2),
  height: '400px',
  overflow: 'hidden',
  background: '#fff',
  borderRadius: '8px',
  boxShadow: '0 4px 12px rgba(0,0,0,0.1)',
  '& .header': {
    background: theme.palette.primary.main,
    margin: -16,
    marginBottom: 16,
    padding: 16,
    color: '#fff',
    borderTopLeftRadius: '8px',
    borderTopRightRadius: '8px',
  },
  '& .content': {
    height: '320px',
    overflow: 'auto',
    '&::-webkit-scrollbar': {
      width: '6px',
    },
    '&::-webkit-scrollbar-track': {
      background: '#f1f1f1',
    },
    '&::-webkit-scrollbar-thumb': {
      background: '#888',
      borderRadius: '3px',
    },
  },
}));

const EventCard = styled(Card)(({ theme }) => ({
  display: 'flex',
  marginBottom: theme.spacing(2),
  overflow: 'hidden',
  boxShadow: '0 2px 8px rgba(0,0,0,0.1)',
  '& .date-box': {
    width: 100,
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center',
    background: theme.palette.primary.main,
    color: '#fff',
    padding: theme.spacing(2),
  },
}));

const StatsCard = styled(Card)(({ theme }) => ({
  textAlign: 'center',
  padding: theme.spacing(3),
  background: '#fff',
  color: theme.palette.primary.main,
  borderRadius: '8px',
  height: '100%',
  transition: 'all 0.3s ease',
  cursor: 'pointer',
  '&:hover': {
    transform: 'translateY(-5px)',
    boxShadow: '0 8px 24px rgba(0,0,0,0.15)',
  },
}));

const QuickLinkCard = styled(Card)(({ theme }) => ({
  height: '100%',
  transition: 'transform 0.3s ease',
  cursor: 'pointer',
  background: '#fff',
  border: `1px solid ${theme.palette.primary.main}`,
  '&:hover': {
    transform: 'translateY(-5px)',
    background: theme.palette.primary.main,
    '& .MuiTypography-root, & .MuiSvgIcon-root': {
      color: '#fff',
    },
  },
}));

const SectionTitle = styled(Typography)(({ theme }) => ({
  position: 'relative',
  paddingBottom: theme.spacing(2),
  marginBottom: theme.spacing(4),
  textAlign: 'center',
  '&::after': {
    content: '""',
    position: 'absolute',
    bottom: 0,
    left: '50%',
    transform: 'translateX(-50%)',
    width: '80px',
    height: '3px',
    background: theme.palette.primary.main,
  },
}));

const Home = () => {
  const theme = useTheme();

  const slides = [
    {
      image: '/slide1.jpg',
      title: "TOPPERS' TOP CHOICE",
      description: 'EXPERIENTIAL LEARNING',
    },
    {
      image: '/slide2.jpg',
      title: 'FANTABULOUS CAMPUS',
      description: 'Modern infrastructure for better learning',
    },
    {
      image: '/slide3.jpg',
      title: '15th POSITION IN TAMILNADU',
      description: 'TNEA 2024 ADMISSIONS',
    },
  ];

  const announcements = [
    {
      date: 'Dec 18, 2024',
      title: 'Inauguration of new facilities - Collab Space, Vista Hall and Synapse Studio',
      type: 'Event',
    },
    {
      date: 'Dec 17, 2024',
      title: 'Welcome Distinguished delegates from Germany',
      type: 'International',
    },
    {
      date: 'Dec 12, 2024',
      title: 'Blood Donation Camp',
      type: 'Social',
    },
  ];

  const events = [
    {
      date: { day: '26', month: 'DEC' },
      title: 'Conclave 2024',
      time: '9:00 AM',
      venue: 'Main Auditorium',
    },
    {
      date: { day: '27', month: 'DEC' },
      title: 'CFRD Seminar',
      time: '10:00 AM',
      venue: 'Seminar Hall',
    },
    {
      date: { day: '06', month: 'JAN' },
      title: 'Future Ready with MERN : Intern Today, Innovate Tomorrow',
      time: '9:00 AM',
      venue: 'IT Lab',
    },
  ];

  const news = [
    {
      date: '29 Jan 2025',
      title: 'Team Eshwarites Win iTech Hackfest 2024',
      description: 'Our students showcased exceptional skills at the national hackathon.',
      tag: 'Achievement',
    },
    {
      date: '28 Jan 2025',
      title: 'Amazon Internship Success',
      description: 'Ajay Subramanian selected for prestigious Amazon internship program.',
      tag: 'Placement',
    },
  ];

  const quickLinks = [
    { title: 'Admissions 2024', icon: <SchoolIcon />, link: '/admissions' },
    { title: 'NIRF', icon: <EmojiEventsIcon />, link: '/nirf' },
    { title: 'NAAC', icon: <WorkIcon />, link: '/naac' },
    { title: 'Research', icon: <ScienceIcon />, link: '/research' },
    { title: 'International Relations', icon: <WorkIcon />, link: '/international' },
    { title: 'Placements', icon: <WorkIcon />, link: '/placements' },
  ];

  const stats = [
    { number: '15th', label: 'Position in TN', icon: <EmojiEventsIcon sx={{ fontSize: 40 }} /> },
    { number: '7th', label: 'Rank in Coimbatore', icon: <GroupIcon sx={{ fontSize: 40 }} /> },
    { number: 'TOP 2%', label: 'Scientists of the World', icon: <ScienceIcon sx={{ fontSize: 40 }} /> },
    { number: '50+', label: 'Global Partnerships', icon: <WorkIcon sx={{ fontSize: 40 }} /> },
  ];

  return (
    <Box>
      <HeroSection>
        <Swiper
          modules={[Navigation, Pagination, Autoplay]}
          navigation
          pagination={{ clickable: true }}
          autoplay={{ delay: 5000 }}
          loop
        >
          {slides.map((slide, index) => (
            <SwiperSlide key={index}>
              <Box sx={{ position: 'relative' }}>
                <img src={slide.image} alt={slide.title} />
                <Box
                  sx={{
                    position: 'absolute',
                    bottom: 0,
                    left: 0,
                    right: 0,
                    background: 'linear-gradient(to top, rgba(0,0,0,0.8), transparent)',
                    color: '#fff',
                    padding: { xs: 3, md: 6 },
                  }}
                >
                  <Container maxWidth="lg">
                    <Typography variant="h3" gutterBottom sx={{ fontWeight: 700 }}>
                      {slide.title}
                    </Typography>
                    <Typography variant="h5">
                      {slide.description}
                    </Typography>
                  </Container>
                </Box>
              </Box>
            </SwiperSlide>
          ))}
        </Swiper>
      </HeroSection>

      {/* Quick Links Section */}
      <Container sx={{ mt: -5, position: 'relative', zIndex: 2 }}>
        <Paper elevation={3} sx={{ p: 3, borderRadius: '8px' }}>
          <Grid container spacing={2}>
            {quickLinks.map((link, index) => (
              <Grid item xs={6} sm={4} md={2} key={index}>
                <Card
                  component={RouterLink}
                  to={link.link}
                  sx={{
                    height: '100%',
                    display: 'flex',
                    flexDirection: 'column',
                    alignItems: 'center',
                    justifyContent: 'center',
                    p: 2,
                    textDecoration: 'none',
                    color: 'inherit',
                    transition: 'all 0.3s ease',
                    '&:hover': {
                      transform: 'translateY(-5px)',
                      bgcolor: 'primary.main',
                      color: '#fff',
                    },
                  }}
                >
                  {link.icon}
                  <Typography variant="subtitle2" align="center" sx={{ mt: 1 }}>
                    {link.title}
                  </Typography>
                </Card>
              </Grid>
            ))}
          </Grid>
        </Paper>
      </Container>

      <Container sx={{ mt: 6 }}>
        <Grid container spacing={4}>
          {/* Announcements Section */}
          <Grid item xs={12} md={4}>
            <AnnouncementCard>
              <Box className="header">
                <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                  <NotificationsIcon />
                  <Typography variant="h6">Announcements</Typography>
                </Box>
              </Box>
              <Box className="content">
                <List>
                  {announcements.map((item, index) => (
                    <ListItem 
                      key={index}
                      sx={{ 
                        borderBottom: '1px solid #eee',
                        '&:last-child': { border: 'none' },
                      }}
                    >
                      <ListItemText
                        primary={
                          <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                            <Typography variant="subtitle2" sx={{ color: 'primary.main' }}>
                              {item.title}
                            </Typography>
                            <Chip 
                              label={item.type}
                              size="small"
                              sx={{ 
                                bgcolor: 'primary.main',
                                color: '#fff',
                                fontSize: '0.7rem',
                              }}
                            />
                          </Box>
                        }
                        secondary={item.date}
                      />
                    </ListItem>
                  ))}
                </List>
              </Box>
            </AnnouncementCard>
          </Grid>

          {/* Events Section */}
          <Grid item xs={12} md={4}>
            <AnnouncementCard>
              <Box className="header">
                <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                  <CalendarTodayIcon />
                  <Typography variant="h6">Upcoming Events</Typography>
                </Box>
              </Box>
              <Box className="content">
                {events.map((event, index) => (
                  <EventCard key={index}>
                    <Box className="date-box">
                      <Typography variant="h4" sx={{ fontWeight: 700 }}>
                        {event.date.day}
                      </Typography>
                      <Typography variant="subtitle2">
                        {event.date.month}
                      </Typography>
                    </Box>
                    <CardContent>
                      <Typography variant="subtitle1" sx={{ fontWeight: 600 }}>
                        {event.title}
                      </Typography>
                      <Typography variant="body2" color="text.secondary">
                        Time: {event.time}
                      </Typography>
                      <Typography variant="body2" color="text.secondary">
                        Venue: {event.venue}
                      </Typography>
                    </CardContent>
                  </EventCard>
                ))}
              </Box>
            </AnnouncementCard>
          </Grid>

          {/* News Section */}
          <Grid item xs={12} md={4}>
            <AnnouncementCard>
              <Box className="header">
                <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                  <NewspaperIcon />
                  <Typography variant="h6">Latest News</Typography>
                </Box>
              </Box>
              <Box className="content">
                <List>
                  {news.map((item, index) => (
                    <ListItem 
                      key={index}
                      sx={{ 
                        borderBottom: '1px solid #eee',
                        '&:last-child': { border: 'none' },
                      }}
                    >
                      <ListItemText
                        primary={
                          <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                            <Typography variant="subtitle2" sx={{ color: 'primary.main' }}>
                              {item.title}
                            </Typography>
                            <Chip 
                              label={item.tag}
                              size="small"
                              sx={{ 
                                bgcolor: 'primary.main',
                                color: '#fff',
                                fontSize: '0.7rem',
                              }}
