import React, { useState, useEffect } from 'react';
import {
  AppBar,
  Toolbar,
  Typography,
  Button,
  IconButton,
  Drawer,
  List,
  ListItem,
  ListItemText,
  Box,
  useTheme,
  useMediaQuery,
  Container,
  useScrollTrigger,
  Menu,
  MenuItem,
  Collapse,
  ListItemIcon,
} from '@mui/material';
import MenuIcon from '@mui/icons-material/Menu';
import { Link as RouterLink } from 'react-router-dom';
import { styled } from '@mui/material/styles';
import KeyboardArrowDownIcon from '@mui/icons-material/KeyboardArrowDown';
import SchoolIcon from '@mui/icons-material/School';
import LibraryBooksIcon from '@mui/icons-material/LibraryBooks';
import GroupIcon from '@mui/icons-material/Group';
import EventIcon from '@mui/icons-material/Event';
import InfoIcon from '@mui/icons-material/Info';
import ContactsIcon from '@mui/icons-material/Contacts';

const TopBar = styled(Box)(({ theme }) => ({
  background: theme.palette.primary.dark,
  color: '#fff',
  padding: theme.spacing(1),
  display: 'flex',
  justifyContent: 'space-between',
  alignItems: 'center',
  [theme.breakpoints.down('md')]: {
    display: 'none',
  },
}));

const StyledAppBar = styled(AppBar)(({ theme, trigger }) => ({
  background: trigger 
    ? 'rgba(255, 255, 255, 0.95)'
    : 'transparent',
  boxShadow: trigger 
    ? '0 2px 28px rgba(0,0,0,0.1)'
    : 'none',
  backdropFilter: trigger ? 'blur(20px)' : 'none',
  transition: 'all 0.3s ease',
  '& .MuiToolbar-root': {
    transition: 'all 0.3s ease',
    minHeight: trigger ? '70px' : '80px',
  },
}));

const Logo = styled(Typography)(({ theme, trigger }) => ({
  fontWeight: 800,
  fontSize: trigger ? '1.5rem' : '1.8rem',
  background: 'linear-gradient(45deg, #1a237e, #0d47a1)',
  WebkitBackgroundClip: 'text',
  WebkitTextFillColor: 'transparent',
  letterSpacing: 1,
  cursor: 'pointer',
  transition: 'all 0.3s ease',
}));

const NavButton = styled(Button)(({ theme, trigger }) => ({
  color: trigger ? theme.palette.primary.main : '#fff',
  marginLeft: theme.spacing(1),
  padding: theme.spacing(1, 2),
  fontSize: '0.9rem',
  fontWeight: 500,
  textTransform: 'none',
  transition: 'all 0.2s ease',
  '&:hover': {
    backgroundColor: 'transparent',
    color: theme.palette.primary.main,
  },
}));

const MobileDrawer = styled(Drawer)(({ theme }) => ({
  '& .MuiDrawer-paper': {
    width: 280,
    background: '#fff',
    color: theme.palette.text.primary,
  },
}));

const SubMenu = styled(Menu)(({ theme }) => ({
  '& .MuiPaper-root': {
    marginTop: '8px',
    minWidth: 180,
    boxShadow: '0 8px 16px rgba(0,0,0,0.1)',
    borderRadius: '8px',
  },
}));

const Navbar = () => {
  const [mobileOpen, setMobileOpen] = useState(false);
  const [anchorEl, setAnchorEl] = useState(null);
  const [menuItem, setMenuItem] = useState(null);
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('md'));
  const trigger = useScrollTrigger({
    disableHysteresis: true,
    threshold: 100,
  });

  const menuItems = [
    {
      text: 'About',
      path: '/about',
      icon: <InfoIcon />,
      submenu: [
        { text: 'Overview', path: '/about/overview' },
        { text: 'Vision & Mission', path: '/about/vision-mission' },
        { text: 'Management', path: '/about/management' },
      ],
    },
    {
      text: 'Academics',
      path: '/academics',
      icon: <LibraryBooksIcon />,
      submenu: [
        { text: 'Departments', path: '/academics/departments' },
        { text: 'Programs', path: '/academics/programs' },
        { text: 'Faculty', path: '/academics/faculty' },
      ],
    },
    {
      text: 'Admissions',
      path: '/admissions',
      icon: <SchoolIcon />,
      submenu: [
        { text: 'Process', path: '/admissions/process' },
        { text: 'Requirements', path: '/admissions/requirements' },
        { text: 'Apply Now', path: '/admissions/apply' },
      ],
    },
    {
      text: 'Campus Life',
      path: '/campus-life',
      icon: <GroupIcon />,
      submenu: [
        { text: 'Facilities', path: '/campus-life/facilities' },
        { text: 'Activities', path: '/campus-life/activities' },
        { text: 'Clubs', path: '/campus-life/clubs' },
      ],
    },
    {
      text: 'Events',
      path: '/events',
      icon: <EventIcon />,
    },
    {
      text: 'Contact',
      path: '/contact',
      icon: <ContactsIcon />,
    },
  ];

  const handleMenuOpen = (event, item) => {
    setAnchorEl(event.currentTarget);
    setMenuItem(item);
  };

  const handleMenuClose = () => {
    setAnchorEl(null);
    setMenuItem(null);
  };

  const handleDrawerToggle = () => {
    setMobileOpen(!mobileOpen);
  };

  const drawer = (
    <Box sx={{ py: 2 }}>
      <Box sx={{ px: 2, mb: 4 }}>
        <Logo variant="h6" component={RouterLink} to="/" trigger={false}>
          COLLEGE NAME
        </Logo>
      </Box>
      <List>
        {menuItems.map((item) => (
          <Box key={item.text}>
            <ListItem 
              button 
              component={RouterLink}
              to={item.path}
              onClick={() => !item.submenu && handleDrawerToggle()}
            >
              <ListItemIcon>{item.icon}</ListItemIcon>
              <ListItemText primary={item.text} />
              {item.submenu && <KeyboardArrowDownIcon />}
            </ListItem>
            {item.submenu && (
              <Collapse in={true} timeout="auto" unmountOnExit>
                <List component="div" disablePadding>
                  {item.submenu.map((subItem) => (
                    <ListItem
                      key={subItem.text}
                      button
                      component={RouterLink}
                      to={subItem.path}
                      onClick={handleDrawerToggle}
                      sx={{ pl: 4 }}
                    >
                      <ListItemText primary={subItem.text} />
                    </ListItem>
                  ))}
                </List>
              </Collapse>
            )}
          </Box>
        ))}
      </List>
    </Box>
  );

  return (
    <>
      <TopBar>
        <Container>
          <Box sx={{ display: 'flex', justifyContent: 'space-between' }}>
            <Typography variant="body2">
              Phone: +1 (555) 123-4567 | Email: info@college.edu
            </Typography>
            <Box>
              <Button 
                color="inherit" 
                size="small"
                component={RouterLink}
                to="/alumni"
              >
                Alumni
              </Button>
              <Button 
                color="inherit" 
                size="small"
                component={RouterLink}
                to="/careers"
              >
                Careers
              </Button>
              <Button 
                color="inherit" 
                size="small"
                component={RouterLink}
                to="/library"
              >
                Library
              </Button>
            </Box>
          </Box>
        </Container>
      </TopBar>
      <StyledAppBar position="sticky" trigger={trigger}>
        <Container>
          <Toolbar disableGutters>
            <Logo
              variant="h6"
              component={RouterLink}
              to="/"
              trigger={trigger}
              sx={{ flexGrow: { xs: 1, md: 0 } }}
            >
              COLLEGE NAME
            </Logo>
            {isMobile ? (
              <IconButton
                color={trigger ? 'primary' : 'inherit'}
                aria-label="open drawer"
                edge="start"
                onClick={handleDrawerToggle}
              >
                <MenuIcon />
              </IconButton>
            ) : (
              <Box sx={{ display: 'flex', ml: 4 }}>
                {menuItems.map((item) => (
                  <Box key={item.text}>
                    <NavButton
                      trigger={trigger}
                      aria-haspopup={item.submenu ? 'true' : undefined}
                      onClick={(e) => item.submenu ? handleMenuOpen(e, item) : null}
                      component={item.submenu ? undefined : RouterLink}
                      to={item.submenu ? undefined : item.path}
                      endIcon={item.submenu ? <KeyboardArrowDownIcon /> : null}
                    >
                      {item.text}
                    </NavButton>
                    {item.submenu && (
                      <SubMenu
                        anchorEl={anchorEl}
                        open={Boolean(anchorEl) && menuItem === item}
                        onClose={handleMenuClose}
                        MenuListProps={{
                          'aria-labelledby': 'basic-button',
                        }}
                      >
                        {item.submenu.map((subItem) => (
                          <MenuItem
                            key={subItem.text}
                            onClick={handleMenuClose}
                            component={RouterLink}
                            to={subItem.path}
                          >
                            {subItem.text}
                          </MenuItem>
                        ))}
                      </SubMenu>
                    )}
                  </Box>
                ))}
              </Box>
            )}
          </Toolbar>
        </Container>
      </StyledAppBar>
      <MobileDrawer
        variant="temporary"
        anchor="left"
        open={mobileOpen}
        onClose={handleDrawerToggle}
        ModalProps={{
          keepMounted: true,
        }}
      >
        {drawer}
      </MobileDrawer>
    </>
  );
};

export default Navbar;
