import React from 'react';
import {
  Box,
  Container,
  Typography,
  Grid,
  Paper,
  Card,
  CardContent,
  CardMedia,
  List,
  ListItem,
  ListItemIcon,
  ListItemText,
  Button,
} from '@mui/material';
import {
  Timeline,
  TimelineItem,
  TimelineSeparator,
  TimelineDot,
  TimelineConnector,
  TimelineContent,
} from '@mui/lab';
import SchoolIcon from '@mui/icons-material/School';
import StarIcon from '@mui/icons-material/Star';
import EmojiEventsIcon from '@mui/icons-material/EmojiEvents';
import GroupsIcon from '@mui/icons-material/Groups';
import PublicIcon from '@mui/icons-material/Public';
import WorkspacePremiumIcon from '@mui/icons-material/WorkspacePremium';

const milestones = [
  {
    year: '2024',
    title: '15th Position in Tamil Nadu',
    description: 'Achieved 15th position in Tamil Nadu TNEA 2024 admissions and 7th rank in Coimbatore region.',
    icon: <EmojiEventsIcon />
  },
  {
    year: '2023',
    title: 'Top 2% Scientists',
    description: 'Faculty recognized among Top 2% Scientists of the World.',
    icon: <StarIcon />
  },
  {
    year: '2022',
    title: 'International Partnerships',
    description: 'Established 50+ partnerships with universities around the world.',
    icon: <PublicIcon />
  },
  {
    year: '2021',
    title: 'Research Excellence',
    description: 'Set up state-of-the-art research centers and innovation ecosystem.',
    icon: <WorkspacePremiumIcon />
  }
];

const stats = [
  {
    count: '15th',
    label: 'Position in Tamil Nadu',
    description: 'TNEA 2024 Admissions'
  },
  {
    count: '7th',
    label: 'Rank in Coimbatore',
    description: 'TNEA 2024 Admissions'
  },
  {
    count: 'Top 2%',
    label: 'Scientists of the World',
    description: 'Faculty Recognition'
  },
  {
    count: '50+',
    label: 'Global Partnerships',
    description: 'International Universities'
  }
];

const features = [
  {
    title: 'Experiential Learning',
    description: 'Hands-on experience through industry projects, internships, and practical training.',
    image: '/images/about/experiential.jpg'
  },
  {
    title: 'Research Excellence',
    description: 'State-of-the-art research facilities and collaboration with global institutions.',
    image: '/images/about/research.jpg'
  },
  {
    title: 'Industry Integration',
    description: 'Strong industry partnerships ensuring real-world exposure and placement opportunities.',
    image: '/images/about/industry.jpg'
  }
];

const About = () => {
  return (
    <Box sx={{ py: 8 }}>
      <Container>
        {/* Hero Section */}
        <Box sx={{ textAlign: 'center', mb: 8 }}>
          <Typography variant="h2" gutterBottom sx={{ fontWeight: 700 }}>
            About Sri Eshwar
          </Typography>
          <Typography variant="h5" color="text.secondary" sx={{ mb: 4 }}>
            Toppers' Top Choice for Engineering Excellence
          </Typography>
        </Box>

        {/* Stats Section */}
        <Grid container spacing={4} sx={{ mb: 8 }}>
          {stats.map((stat, index) => (
            <Grid item xs={12} sm={6} md={3} key={index}>
              <Paper
                sx={{
                  p: 3,
                  textAlign: 'center',
                  height: '100%',
                  transition: 'transform 0.3s ease',
                  '&:hover': {
                    transform: 'translateY(-5px)',
                  },
                }}
              >
                <Typography variant="h3" gutterBottom sx={{ fontWeight: 700, color: 'primary.main' }}>
                  {stat.count}
                </Typography>
                <Typography variant="h6" gutterBottom>
                  {stat.label}
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  {stat.description}
                </Typography>
              </Paper>
            </Grid>
          ))}
        </Grid>

        {/* Features Section */}
        <Box sx={{ mb: 8 }}>
          <Typography variant="h3" gutterBottom sx={{ fontWeight: 700, textAlign: 'center', mb: 4 }}>
            Why Choose Sri Eshwar?
          </Typography>
          <Grid container spacing={4}>
            {features.map((feature, index) => (
              <Grid item xs={12} md={4} key={index}>
                <Card
                  sx={{
                    height: '100%',
                    transition: 'transform 0.3s ease',
                    '&:hover': {
                      transform: 'translateY(-5px)',
                    },
                  }}
                >
                  <CardMedia
                    component="img"
                    height="200"
                    image={feature.image}
                    alt={feature.title}
                  />
                  <CardContent>
                    <Typography variant="h5" gutterBottom>
                      {feature.title}
                    </Typography>
                    <Typography variant="body1" color="text.secondary">
                      {feature.description}
                    </Typography>
                  </CardContent>
                </Card>
              </Grid>
            ))}
          </Grid>
        </Box>

        {/* Timeline Section */}
        <Box sx={{ mb: 8 }}>
          <Typography variant="h3" gutterBottom sx={{ fontWeight: 700, textAlign: 'center', mb: 4 }}>
            Our Journey
          </Typography>
          <Timeline position="alternate">
            {milestones.map((milestone, index) => (
              <TimelineItem key={index}>
                <TimelineSeparator>
                  <TimelineDot color="primary">
                    {milestone.icon}
                  </TimelineDot>
                  {index !== milestones.length - 1 && <TimelineConnector />}
                </TimelineSeparator>
                <TimelineContent>
                  <Paper
                    sx={{
                      p: 3,
                      transition: 'transform 0.3s ease',
                      '&:hover': {
                        transform: 'translateY(-5px)',
                      },
                    }}
                  >
                    <Typography variant="h6" component="span">
                      {milestone.year}
                    </Typography>
                    <Typography variant="h5" sx={{ color: 'primary.main', my: 1 }}>
                      {milestone.title}
                    </Typography>
                    <Typography>{milestone.description}</Typography>
                  </Paper>
                </TimelineContent>
              </TimelineItem>
            ))}
          </Timeline>
        </Box>

        {/* CTA Section */}
        <Box sx={{ textAlign: 'center' }}>
          <Paper sx={{ p: 4, bgcolor: 'primary.main', color: 'white' }}>
            <Typography variant="h4" gutterBottom>
              Join Our Journey of Excellence
            </Typography>
            <Typography variant="body1" paragraph>
              Experience world-class education and shape your future with Sri Eshwar
            </Typography>
            <Button
              variant="contained"
              color="secondary"
              size="large"
              href="/admissions"
              sx={{ mt: 2 }}
            >
              Apply Now
            </Button>
          </Paper>
        </Box>
      </Container>
    </Box>
  );
};

export default About;
