import React from 'react';
import { 
  Box, 
  Container, 
  Typography, 
  Grid, 
  Card, 
  CardContent,
  Button,
  useTheme,
  IconButton,
  Divider,
  Paper,
} from '@mui/material';
import { motion } from 'framer-motion';
import SchoolIcon from '@mui/icons-material/School';
import EventIcon from '@mui/icons-material/Event';
import LibraryBooksIcon from '@mui/icons-material/LibraryBooks';
import ArrowForwardIcon from '@mui/icons-material/ArrowForward';
import { Swiper, SwiperSlide } from 'swiper/react';
import { Navigation, Pagination, Autoplay } from 'swiper/modules';
import 'swiper/css';
import 'swiper/css/navigation';
import 'swiper/css/pagination';
import { styled } from '@mui/material/styles';

const HeroSection = styled(Box)(({ theme }) => ({
  position: 'relative',
  overflow: 'hidden',
  '& .swiper': {
    width: '100%',
    height: '600px',
  },
  '& .swiper-slide img': {
    width: '100%',
    height: '100%',
    objectFit: 'cover',
  },
  '& .swiper-pagination-bullet': {
    width: '12px',
    height: '12px',
    background: '#fff',
    opacity: 0.5,
    '&-active': {
      opacity: 1,
    },
  },
}));

const NewsCard = styled(Paper)(({ theme }) => ({
  padding: theme.spacing(2),
  height: '400px',
  overflow: 'auto',
  background: '#f8f9fa',
  borderRadius: '10px',
  '& .news-item': {
    padding: theme.spacing(2),
    borderBottom: '1px solid #dee2e6',
    '&:last-child': {
      borderBottom: 'none',
    },
  },
}));

const StatsCard = styled(Card)(({ theme }) => ({
  textAlign: 'center',
  padding: theme.spacing(3),
  background: 'linear-gradient(135deg, #1a237e 0%, #0d47a1 100%)',
  color: '#fff',
  borderRadius: '10px',
  height: '100%',
}));

const QuickLinkCard = styled(Card)(({ theme }) => ({
  height: '100%',
  transition: 'transform 0.3s ease',
  cursor: 'pointer',
  '&:hover': {
    transform: 'translateY(-5px)',
  },
}));

const Home = () => {
  const theme = useTheme();

  const slides = [
    {
      image: '/slide1.jpg',
      title: 'Welcome to Our College',
      description: 'Empowering minds, shaping futures',
    },
    {
      image: '/slide2.jpg',
      title: 'State-of-the-art Facilities',
      description: 'Modern infrastructure for better learning',
    },
    {
      image: '/slide3.jpg',
      title: 'Research Excellence',
      description: 'Pushing boundaries of innovation',
    },
  ];

  const news = [
    {
      date: '29 Jan 2025',
      title: 'Campus Placement Drive',
      description: 'Major tech companies visiting for recruitment',
    },
    {
      date: '28 Jan 2025',
      title: 'Research Paper Published',
      description: 'Students publish paper in international journal',
    },
    {
      date: '27 Jan 2025',
      title: 'Sports Tournament',
      description: 'Annual inter-college sports meet announced',
    },
    {
      date: '26 Jan 2025',
      title: 'Republic Day Celebration',
      description: 'Grand celebration on campus',
    },
  ];

  const stats = [
    { number: '15000+', label: 'Students' },
    { number: '200+', label: 'Faculty' },
    { number: '100%', label: 'Placement' },
    { number: '50+', label: 'Companies' },
  ];

  const quickLinks = [
    { title: 'Admissions', icon: <SchoolIcon />, link: '/admissions' },
    { title: 'Academics', icon: <LibraryBooksIcon />, link: '/academics' },
    { title: 'Research', icon: <LibraryBooksIcon />, link: '/research' },
    { title: 'Campus Life', icon: <EventIcon />, link: '/campus-life' },
  ];

  return (
    <Box>
      <HeroSection>
        <Swiper
          modules={[Navigation, Pagination, Autoplay]}
          navigation
          pagination={{ clickable: true }}
          autoplay={{ delay: 5000 }}
          loop
        >
          {slides.map((slide, index) => (
            <SwiperSlide key={index}>
              <Box sx={{ position: 'relative' }}>
                <img src={slide.image} alt={slide.title} />
                <Box
                  sx={{
                    position: 'absolute',
                    bottom: 0,
                    left: 0,
                    right: 0,
                    background: 'rgba(0,0,0,0.5)',
                    color: '#fff',
                    padding: 4,
                  }}
                >
                  <Container>
                    <Typography variant="h3" gutterBottom>
                      {slide.title}
                    </Typography>
                    <Typography variant="h6">
                      {slide.description}
                    </Typography>
                  </Container>
