import React from 'react';
import {
  Container,
  Typography,
  Grid,
  Card,
  CardContent,
  Button,
  Box,
  Chip,
} from '@mui/material';
import { motion } from 'framer-motion';

const courses = [
  {
    id: 1,
    title: 'Computer Science',
    description: 'Learn programming, algorithms, and software development',
    gradient: 'linear-gradient(45deg, #2196F3 30%, #21CBF3 90%)',
    duration: '4 Years',
    level: "Bachelor's Degree",
  },
  {
    id: 2,
    title: 'Business Administration',
    description: 'Study management, marketing, and business strategy',
    gradient: 'linear-gradient(45deg, #4CAF50 30%, #8BC34A 90%)',
    duration: '4 Years',
    level: "Bachelor's Degree",
  },
  {
    id: 3,
    title: 'Mechanical Engineering',
    description: 'Study mechanics, thermodynamics, and machine design',
    gradient: 'linear-gradient(45deg, #FF9800 30%, #FFC107 90%)',
    duration: '4 Years',
    level: "Bachelor's Degree",
  },
  {
    id: 4,
    title: 'Data Science',
    description: 'Learn data analysis, machine learning, and statistics',
    gradient: 'linear-gradient(45deg, #9C27B0 30%, #E040FB 90%)',
    duration: '2 Years',
    level: "Master's Degree",
  },
  {
    id: 5,
    title: 'Digital Marketing',
    description: 'Master social media, SEO, and online advertising',
    gradient: 'linear-gradient(45deg, #F44336 30%, #FF5722 90%)',
    duration: '2 Years',
    level: "Master's Degree",
  },
  {
    id: 6,
    title: 'Architecture',
    description: 'Study design, construction, and urban planning',
    gradient: 'linear-gradient(45deg, #795548 30%, #A1887F 90%)',
    duration: '5 Years',
    level: "Bachelor's Degree",
  },
];

const Courses = () => {
  return (
    <Box sx={{ py: 8 }}>
      <Container>
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
        >
          <Typography variant="h2" gutterBottom align="center">
            Our Courses
          </Typography>
          <Typography variant="h5" paragraph align="center" color="text.secondary">
            Discover Your Perfect Program
          </Typography>
        </motion.div>

        <Grid container spacing={4} sx={{ mt: 4 }}>
          {courses.map((course, index) => (
            <Grid item xs={12} md={6} lg={4} key={course.id}>
              <motion.div
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.5, delay: index * 0.1 }}
              >
                <Card 
                  sx={{ 
                    height: '100%',
                    display: 'flex',
                    flexDirection: 'column',
                    '&:hover': {
                      transform: 'translateY(-8px)',
                      transition: 'transform 0.3s ease-in-out',
                    },
                  }}
                >
                  <Box
                    sx={{
                      height: 200,
                      background: course.gradient,
                      display: 'flex',
                      alignItems: 'center',
                      justifyContent: 'center',
                    }}
                  >
                    <Typography variant="h4" sx={{ color: 'white', textShadow: '2px 2px 4px rgba(0,0,0,0.3)' }}>
                      {course.title}
                    </Typography>
                  </Box>
                  <CardContent sx={{ flexGrow: 1 }}>
                    <Typography paragraph color="text.secondary">
                      {course.description}
                    </Typography>
                    <Box sx={{ mb: 2 }}>
                      <Chip 
                        label={course.duration} 
                        size="small" 
                        sx={{ mr: 1 }} 
                      />
                      <Chip 
                        label={course.level} 
                        size="small" 
                        color="primary" 
                      />
                    </Box>
                    <Button 
                      variant="contained" 
                      color="primary"
                      fullWidth
                    >
                      Learn More
                    </Button>
                  </CardContent>
                </Card>
              </motion.div>
            </Grid>
          ))}
        </Grid>
      </Container>
    </Box>
  );
};

export default Courses;
