import React from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Box,
  Container,
  Typography,
  Grid,
  Paper,
  Card,
  CardContent,
  Avatar,
  Button,
  IconButton,
  Divider,
} from '@mui/material';
import {
  School,
  Star,
  Timeline,
  EmojiEvents,
  CheckCircle,
  ArrowForward,
  People,
  Lightbulb,
  Build,
  Computer,
  LocalLibrary,
  Business,
  Group,
  Psychology,
} from '@mui/icons-material';

const About = () => {
  const navigate = useNavigate();

  const handleExplorePrograms = () => {
    navigate('/academics#programs');
  };

  const stats = [
    { count: '5000+', label: 'Students', icon: People },
