import React from 'react';
import {
  Box,
  Container,
  Typography,
  Grid,
  Card,
  CardContent,
  CardMedia,
  Chip,
  Button,
  IconButton,
} from '@mui/material';
import { styled } from '@mui/material/styles';
import { motion } from 'framer-motion';
import CalendarTodayIcon from '@mui/icons-material/CalendarToday';
import LocationOnIcon from '@mui/icons-material/LocationOn';
import ShareIcon from '@mui/icons-material/Share';
import BookmarkBorderIcon from '@mui/icons-material/BookmarkBorder';

const PageHeader = styled(Box)(({ theme }) => ({
  background: 'linear-gradient(135deg, #1a237e 0%, #0d47a1 100%)',
  padding: theme.spacing(10, 0),
  marginBottom: theme.spacing(6),
  position: 'relative',
  overflow: 'hidden',
  '&::before': {
    content: '""',
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    background: 'url("/pattern.png")',
    opacity: 0.1,
  },
}));

const EventCard = styled(Card)(({ theme }) => ({
  height: '100%',
  display: 'flex',
  flexDirection: 'column',
  borderRadius: '16px',
  overflow: 'hidden',
  background: '#ffffff',
  boxShadow: '0 8px 40px rgba(0,0,0,0.12)',
  transition: 'all 0.3s cubic-bezier(0.4, 0, 0.2, 1)',
  '&:hover': {
    transform: 'translateY(-12px)',
    boxShadow: '0 12px 50px rgba(0,0,0,0.16)',
  },
}));

const EventImage = styled(CardMedia)(({ theme }) => ({
  paddingTop: '56.25%', // 16:9 aspect ratio
  position: 'relative',
  '&::after': {
    content: '""',
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    background: 'linear-gradient(to bottom, rgba(0,0,0,0) 0%, rgba(0,0,0,0.4) 100%)',
  },
}));

const EventChip = styled(Chip)(({ theme }) => ({
  borderRadius: '20px',
  fontWeight: 600,
  '&.upcoming': {
    background: 'linear-gradient(45deg, #4CAF50 30%, #81C784 90%)',
    color: 'white',
  },
  '&.featured': {
    background: 'linear-gradient(45deg, #FFC107 30%, #FFD54F 90%)',
    color: 'white',
  },
}));

const IconContainer = styled(Box)(({ theme }) => ({
  display: 'flex',
  alignItems: 'center',
  color: theme.palette.text.secondary,
  marginBottom: theme.spacing(1),
  '& svg': {
    marginRight: theme.spacing(1),
    fontSize: '1.2rem',
  },
}));

const Events = () => {
  const events = [
    {
      id: 1,
      title: 'Annual Tech Symposium',
      date: 'March 15, 2024',
      location: 'Main Auditorium',
      image: '/event1.jpg',
      description: 'Join us for a day of cutting-edge technology discussions and networking opportunities with industry leaders.',
      category: 'upcoming',
    },
    {
      id: 2,
      title: 'Cultural Festival',
      date: 'April 5-7, 2024',
      location: 'College Campus',
      image: '/event2.jpg',
      description: 'Three days of music, dance, art, and celebration showcasing diverse talents from our college community.',
      category: 'featured',
    },
    {
      id: 3,
      title: 'Research Conference',
      date: 'May 20, 2024',
      location: 'Conference Center',
      image: '/event3.jpg',
      description: 'Present your research findings and connect with fellow researchers in this annual conference.',
      category: 'upcoming',
    },
    {
      id: 4,
      title: 'Sports Tournament',
      date: 'June 10-15, 2024',
      location: 'Sports Complex',
      image: '/event4.jpg',
      description: 'Annual inter-college sports competition featuring various sports and athletics events.',
      category: 'upcoming',
    },
  ];

  return (
    <Box>
      <PageHeader>
        <Container>
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8 }}
          >
            <Typography 
              variant="h2" 
              color="white" 
              gutterBottom
              sx={{ 
                fontWeight: 800,
                textAlign: 'center',
                textShadow: '0 2px 4px rgba(0,0,0,0.2)',
              }}
            >
              Upcoming Events
            </Typography>
            <Typography 
              variant="h5" 
              color="rgba(255,255,255,0.9)" 
              textAlign="center"
              sx={{ maxWidth: 800, mx: 'auto' }}
            >
              Stay connected with our vibrant community through various events and activities
            </Typography>
          </motion.div>
        </Container>
      </PageHeader>

      <Container sx={{ mb: 8 }}>
        <Grid container spacing={4}>
          {events.map((event, index) => (
            <Grid item xs={12} md={4} key={event.id}>
              <motion.div
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.5, delay: index * 0.2 }}
              >
                <EventCard>
                  <EventImage
                    image={event.image}
                    title={event.title}
                  />
                  <CardContent sx={{ flexGrow: 1, p: 3 }}>
                    <Box sx={{ mb: 2 }}>
                      <EventChip
                        label={event.category === 'upcoming' ? 'Upcoming' : 'Featured'}
                        className={event.category}
                        size="small"
                      />
                    </Box>
                    <Typography 
                      variant="h5" 
                      gutterBottom
                      sx={{ 
                        fontWeight: 700,
                        mb: 2,
                      }}
                    >
                      {event.title}
                    </Typography>
                    <IconContainer>
                      <CalendarTodayIcon />
                      <Typography variant="body2">
                        {event.date}
                      </Typography>
                    </IconContainer>
                    <IconContainer>
                      <LocationOnIcon />
                      <Typography variant="body2">
                        {event.location}
                      </Typography>
                    </IconContainer>
                    <Typography 
                      variant="body2" 
                      color="text.secondary"
                      sx={{ 
                        mb: 3,
                        lineHeight: 1.7,
                      }}
                    >
                      {event.description}
                    </Typography>
                    <Box sx={{ 
                      display: 'flex', 
                      justifyContent: 'space-between',
                      alignItems: 'center',
                    }}>
                      <Button 
                        variant="contained" 
                        color="primary"
                        sx={{ 
                          borderRadius: '20px',
                          textTransform: 'none',
                          px: 3,
                        }}
                      >
                        Register Now
                      </Button>
                      <Box>
                        <IconButton size="small" sx={{ mr: 1 }}>
                          <ShareIcon />
                        </IconButton>
                        <IconButton size="small">
                          <BookmarkBorderIcon />
                        </IconButton>
                      </Box>
                    </Box>
                  </CardContent>
                </EventCard>
              </motion.div>
            </Grid>
          ))}
        </Grid>
      </Container>
    </Box>
  );
};

export default Events;
