import React from 'react';
import { 
  Box, 
  Container, 
  Typography, 
  Grid, 
  Card, 
  CardContent,
  Button,
  useTheme,
  IconButton,
  Paper,
} from '@mui/material';
import { motion } from 'framer-motion';
import SchoolIcon from '@mui/icons-material/School';
import EventIcon from '@mui/icons-material/Event';
import LibraryBooksIcon from '@mui/icons-material/LibraryBooks';
import ArrowForwardIcon from '@mui/icons-material/ArrowForward';
import { Swiper, SwiperSlide } from 'swiper/react';
import { Navigation, Pagination, Autoplay } from 'swiper/modules';
import 'swiper/css';
import 'swiper/css/navigation';
import 'swiper/css/pagination';
import { styled } from '@mui/material/styles';
import GroupIcon from '@mui/icons-material/Group';
import WorkIcon from '@mui/icons-material/Work';
import ScienceIcon from '@mui/icons-material/Science';

const HeroSection = styled(Box)(({ theme }) => ({
  position: 'relative',
  overflow: 'hidden',
  '& .swiper': {
    width: '100%',
    height: '600px',
    [theme.breakpoints.down('md')]: {
      height: '400px',
    },
  },
  '& .swiper-slide img': {
    width: '100%',
    height: '100%',
    objectFit: 'cover',
  },
  '& .swiper-pagination-bullet': {
    width: '12px',
    height: '12px',
    background: '#fff',
    opacity: 0.5,
    '&-active': {
      opacity: 1,
      background: theme.palette.primary.main,
    },
  },
  '& .swiper-button-prev, & .swiper-button-next': {
    color: '#fff',
    background: 'rgba(0,0,0,0.3)',
    width: '40px',
    height: '40px',
    borderRadius: '50%',
    '&::after': {
      fontSize: '20px',
    },
    '&:hover': {
      background: theme.palette.primary.main,
    },
  },
}));

const NewsCard = styled(Paper)(({ theme }) => ({
  height: '500px',
  overflow: 'hidden',
  background: '#fff',
  borderRadius: '8px',
  boxShadow: '0 4px 12px rgba(0,0,0,0.1)',
  '& .news-header': {
    background: theme.palette.primary.main,
    color: '#fff',
    padding: theme.spacing(2),
    borderTopLeftRadius: '8px',
    borderTopRightRadius: '8px',
  },
  '& .news-content': {
    height: '420px',
    overflow: 'auto',
    padding: theme.spacing(0, 2),
    '&::-webkit-scrollbar': {
      width: '6px',
    },
    '&::-webkit-scrollbar-track': {
      background: '#f1f1f1',
    },
    '&::-webkit-scrollbar-thumb': {
      background: '#888',
      borderRadius: '3px',
    },
  },
  '& .news-item': {
    padding: theme.spacing(2),
    borderBottom: '1px solid #eee',
    transition: 'all 0.3s ease',
    '&:hover': {
      background: '#f8f9fa',
    },
    '&:last-child': {
      borderBottom: 'none',
    },
  },
}));

const StatsCard = styled(Card)(({ theme }) => ({
  textAlign: 'center',
  padding: theme.spacing(3),
  background: '#fff',
  color: theme.palette.primary.main,
  borderRadius: '8px',
  height: '100%',
  transition: 'all 0.3s ease',
  cursor: 'pointer',
  '&:hover': {
    transform: 'translateY(-5px)',
    boxShadow: '0 8px 24px rgba(0,0,0,0.15)',
  },
}));

const QuickLinkCard = styled(Card)(({ theme }) => ({
  height: '100%',
  transition: 'transform 0.3s ease',
  cursor: 'pointer',
  background: '#fff',
  border: `1px solid ${theme.palette.primary.main}`,
  '&:hover': {
    transform: 'translateY(-5px)',
    background: theme.palette.primary.main,
    '& .MuiTypography-root, & .MuiSvgIcon-root': {
      color: '#fff',
    },
  },
}));

const SectionTitle = styled(Typography)(({ theme }) => ({
  position: 'relative',
  paddingBottom: theme.spacing(2),
  marginBottom: theme.spacing(4),
  textAlign: 'center',
  '&::after': {
    content: '""',
    position: 'absolute',
    bottom: 0,
    left: '50%',
    transform: 'translateX(-50%)',
    width: '80px',
    height: '3px',
    background: theme.palette.primary.main,
  },
}));

const Home = () => {
  const theme = useTheme();

  const slides = [
    {
      image: '/slide1.jpg',
      title: 'Welcome to Our College',
      description: 'Empowering minds, shaping futures',
    },
    {
      image: '/slide2.jpg',
      title: 'State-of-the-art Facilities',
      description: 'Modern infrastructure for better learning',
    },
    {
      image: '/slide3.jpg',
      title: 'Research Excellence',
      description: 'Pushing boundaries of innovation',
    },
  ];

  const news = [
    {
      date: '29 Jan 2025',
      title: 'Campus Placement Drive',
      description: 'Leading tech companies visiting for recruitment. Don\'t miss this opportunity to kickstart your career!',
      tag: 'Placements',
    },
    {
      date: '28 Jan 2025',
      title: 'International Conference on Technology',
      description: 'Join us for the annual international conference featuring renowned speakers from around the globe.',
      tag: 'Events',
    },
    {
      date: '27 Jan 2025',
      title: 'New Research Center Inauguration',
      description: 'State-of-the-art research facility to be inaugurated by industry leaders.',
      tag: 'Research',
    },
    {
      date: '26 Jan 2025',
      title: 'Republic Day Celebration',
      description: 'Grand celebration with cultural performances and flag hoisting ceremony.',
      tag: 'Campus Life',
    },
  ];

  const stats = [
    { number: '15000+', label: 'Students', icon: <SchoolIcon sx={{ fontSize: 40 }} /> },
    { number: '200+', label: 'Faculty', icon: <GroupIcon sx={{ fontSize: 40 }} /> },
    { number: '100%', label: 'Placement', icon: <WorkIcon sx={{ fontSize: 40 }} /> },
    { number: '50+', label: 'Research Centers', icon: <ScienceIcon sx={{ fontSize: 40 }} /> },
  ];

  const quickLinks = [
    { title: 'Admissions', icon: <SchoolIcon />, link: '/admissions' },
    { title: 'Academics', icon: <LibraryBooksIcon />, link: '/academics' },
    { title: 'Research', icon: <ScienceIcon />, link: '/research' },
    { title: 'Campus Life', icon: <EventIcon />, link: '/campus-life' },
  ];

  return (
    <Box>
      <HeroSection>
        <Swiper
          modules={[Navigation, Pagination, Autoplay]}
          navigation
          pagination={{ clickable: true }}
          autoplay={{ delay: 5000 }}
          loop
        >
          {slides.map((slide, index) => (
            <SwiperSlide key={index}>
              <Box sx={{ position: 'relative' }}>
                <img src={slide.image} alt={slide.title} />
                <Box
                  sx={{
                    position: 'absolute',
                    bottom: 0,
                    left: 0,
                    right: 0,
                    background: 'linear-gradient(to top, rgba(0,0,0,0.8), transparent)',
                    color: '#fff',
                    padding: { xs: 3, md: 6 },
                  }}
                >
                  <Container maxWidth="lg">
                    <Typography variant="h3" gutterBottom sx={{ fontWeight: 700 }}>
                      {slide.title}
                    </Typography>
                    <Typography variant="h5">
                      {slide.description}
                    </Typography>
                  </Container>
                </Box>
              </Box>
            </SwiperSlide>
          ))}
        </Swiper>
      </HeroSection>

      <Container sx={{ mt: 6, mb: 6 }}>
        <Grid container spacing={4}>
          <Grid item xs={12} md={8}>
            <NewsCard elevation={3}>
              <Box className="news-header">
                <Typography variant="h5" sx={{ fontWeight: 600 }}>
                  Latest News & Updates
                </Typography>
              </Box>
              <Box className="news-content">
                {news.map((item, index) => (
                  <Box key={index} className="news-item">
                    <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 1 }}>
                      <Typography variant="caption" color="primary" sx={{ fontWeight: 500 }}>
                        {item.date}
                      </Typography>
                      <Typography 
                        variant="caption" 
                        sx={{ 
                          bgcolor: 'primary.main', 
                          color: '#fff', 
                          px: 1, 
                          py: 0.5, 
                          borderRadius: 1,
                          fontSize: '0.7rem',
                        }}
                      >
                        {item.tag}
                      </Typography>
                    </Box>
                    <Typography variant="h6" gutterBottom sx={{ fontWeight: 600 }}>
                      {item.title}
                    </Typography>
                    <Typography variant="body2" color="text.secondary" sx={{ mb: 1 }}>
                      {item.description}
                    </Typography>
                    <Button
                      endIcon={<ArrowForwardIcon />}
                      size="small"
                      sx={{ 
                        color: 'primary.main',
                        '&:hover': {
                          background: 'rgba(0,74,173,0.08)',
                        },
                      }}
                    >
                      Read More
                    </Button>
                  </Box>
                ))}
              </Box>
            </NewsCard>
          </Grid>
          <Grid item xs={12} md={4}>
            <Paper sx={{ p: 3, height: '100%', borderRadius: '8px' }}>
              <Typography variant="h5" gutterBottom sx={{ fontWeight: 600, color: 'primary.main' }}>
                Quick Links
              </Typography>
              <Grid container spacing={2}>
                {quickLinks.map((link, index) => (
                  <Grid item xs={6} key={index}>
                    <QuickLinkCard>
                      <CardContent sx={{ textAlign: 'center' }}>
                        <IconButton 
                          sx={{ 
                            mb: 1, 
                            color: 'primary.main',
                            '&:hover': { background: 'transparent' },
                          }}
                        >
                          {link.icon}
                        </IconButton>
                        <Typography variant="subtitle1" sx={{ fontWeight: 500 }}>
                          {link.title}
                        </Typography>
                      </CardContent>
                    </QuickLinkCard>
                  </Grid>
                ))}
              </Grid>
            </Paper>
          </Grid>
        </Grid>
      </Container>

      <Box sx={{ bgcolor: '#f8f9fa', py: 8 }}>
        <Container>
          <SectionTitle variant="h4" sx={{ fontWeight: 600 }}>
            Our Achievements
          </SectionTitle>
          <Grid container spacing={4}>
            {stats.map((stat, index) => (
              <Grid item xs={6} md={3} key={index}>
                <StatsCard>
                  <Box sx={{ mb: 2 }}>
                    {stat.icon}
                  </Box>
                  <Typography variant="h3" gutterBottom sx={{ fontWeight: 700 }}>
                    {stat.number}
                  </Typography>
                  <Typography variant="h6" sx={{ fontWeight: 500 }}>
                    {stat.label}
                  </Typography>
                </StatsCard>
              </Grid>
            ))}
          </Grid>
        </Container>
      </Box>

      <Container sx={{ mt: 8, mb: 8 }}>
        <SectionTitle variant="h4" sx={{ fontWeight: 600 }}>
          Campus Highlights
        </SectionTitle>
        <Grid container spacing={4}>
          <Grid item xs={12} md={4}>
            <Card sx={{ height: '100%' }}>
              <Box sx={{ position: 'relative', paddingTop: '66.67%' }}>
                <Box
                  component="img"
                  src="/campus1.jpg"
                  alt="Campus"
                  sx={{
                    position: 'absolute',
                    top: 0,
                    left: 0,
                    width: '100%',
                    height: '100%',
                    objectFit: 'cover',
                  }}
                />
              </Box>
              <CardContent>
                <Typography variant="h6" gutterBottom sx={{ fontWeight: 600 }}>
                  Modern Infrastructure
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  State-of-the-art facilities equipped with the latest technology for enhanced learning experience
                </Typography>
              </CardContent>
            </Card>
          </Grid>
          <Grid item xs={12} md={4}>
            <Card sx={{ height: '100%' }}>
              <Box sx={{ position: 'relative', paddingTop: '66.67%' }}>
                <Box
                  component="img"
                  src="/lab.jpg"
                  alt="Laboratory"
                  sx={{
                    position: 'absolute',
                    top: 0,
                    left: 0,
                    width: '100%',
                    height: '100%',
                    objectFit: 'cover',
                  }}
                />
              </Box>
              <CardContent>
                <Typography variant="h6" gutterBottom sx={{ fontWeight: 600 }}>
                  Advanced Laboratories
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  Well-equipped laboratories with modern instruments for practical learning and research
                </Typography>
              </CardContent>
            </Card>
          </Grid>
          <Grid item xs={12} md={4}>
            <Card sx={{ height: '100%' }}>
              <Box sx={{ position: 'relative', paddingTop: '66.67%' }}>
                <Box
                  component="img"
                  src="/library.jpg"
                  alt="Library"
                  sx={{
                    position: 'absolute',
                    top: 0,
                    left: 0,
                    width: '100%',
                    height: '100%',
                    objectFit: 'cover',
                  }}
                />
              </Box>
              <CardContent>
                <Typography variant="h6" gutterBottom sx={{ fontWeight: 600 }}>
                  Digital Library
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  Extensive collection of digital and print resources for comprehensive learning
                </Typography>
              </CardContent>
            </Card>
          </Grid>
        </Grid>
      </Container>
    </Box>
  );
};

export default Home;
